#include <stdio.h>
#include <math.h>
int main(){
	int i,n;float x,y;
	scanf("%d",&n);
	for(i=1;i<=n;i++){
		scanf("%f",&x);
		if(x<-2) {
			y=x*x-sin(x);
		}else if(x<=2){
			y=pow(2,x)+x;
		}else{
			y=sqrt(x*x+x+1);
		}
		printf("%.2f\n",y);
	}
	return 0;

}

#include <stdio.h>
#include <math.h>
void f(float * a,int n,float * max,float * min){
   	int i;*max=*min=*a;
   	for(i=1;i<n;i++){
		if(a[i]>*max) *max=a[i];
		if(a[i]<*min) *min=a[i];

	}
}
int main(){
	int i,n;float a[1024],M,m;
	scanf("%d",&n);
	for(i=0;i<n;i++){
		scanf("%f",&a[i]);
	}
	f(a,n,&M,&m);
	printf("%.2f %.2f\n",M,m);
	return 0;

}
